import React, { useState } from "react";
import "./Register.css";

const Register = () => {
  const [formChange, setFormChange] = useState({
    name: "",
    email: "",
    number: "",
  });

  const name = formChange.name;
  const mname = formChange.mname;
  const lname = formChange.lname;
  const email = formChange.email;
  const number = formChange.number;

  const [finalChange, setFinalChange] = useState({
    finalFname: "",
    finalMname: "",
    finalLname: "",
    finalEmail: "",
    finalNumber: "",
  });

  const change = (event) => {
    event.preventDefault();
    const val = event.target.value;
    const nam = event.target.name;

    if (nam == "fname") {
      setFormChange({
        ...formChange,
        fname: val,
      });
    }

    if (nam == "mname") {
      setFormChange({
        ...formChange,
        mname: val,
      });
    }

    if (nam == "lname") {
      setFormChange({
        ...formChange,
        lname: val,
      });
    }

    if (nam == "email") {
      setFormChange({
        ...formChange,
        email: val,
      });
    }

    if (nam == "number") {
      setFormChange({
        ...formChange,
        number: val,
      });
    }
  };

  const formSubmit = (event) => {
    event.preventDefault();

    setFinalChange({
      finalFname: fname,
      finalMname: mname,
      finalLname: lname,
      finalEmail: email,
      finalNumber: number,
    });
  };

  // console.log(formChange.fname);

  return (
    <>
      <div className="register-form">
        <form>
          <input
            value={formChange.fname}
            type="text"
            placeholder="First Name"
            autoComplete="off"
            name="fname"
            onChange={change}
            // required
          />

          <input
            value={formChange.mname}
            type="text"
            placeholder="Middle Name"
            autoComplete="off"
            name="mname"
            onChange={change}
          />

          <input
            value={formChange.lname}
            type="text"
            placeholder="Last Name"
            autoComplete="off"
            name="lname"
            onChange={change}
            // required
          />

          <input
            value={formChange.email}
            type="email"
            placeholder="Email Address"
            autoComplete="off"
            name="email"
            onChange={change}
            // required
          />

          <input
            value={formChange.number}
            type="number"
            placeholder="Phone Number"
            autoComplete="off"
            name="number"
            onChange={change}
            // required
          />

          <input
            className="photo-input"
            type="file"
            accept="image/png, image/jpeg, image/jpg"
            placeholder="Photo"
            autoComplete="off"
            name="photo"
            onChange={change}
            // required
          />

          <button type="submit" onClick={formSubmit}>
            Submit
          </button>
        </form>
      </div>

      <h4>Your details are: </h4>

      <p>Name: {finalChange.finalFname}</p>
      <p>Email: {finalChange.finalEmail}</p>
      <p>Number: {finalChange.finalNumber}</p>
    </>
  );
};

export default Register;
