import React, { useState } from "react";
import "./Register.css";

const Register = () => {
  const [formChange, setFormChange] = useState({
    fname: "",
    mname: "",
    lname: "",
    email: "",
    number: "",
  });

  const change = (event) => {
    const val = event.target.value;
    const nam = event.target.name;

    if (nam == "fname") {
      setFormChange({
        fname: val,
      });
    }

    if (nam == "mname") {
      setFormChange({
        mname: val,
      });
    }

    if (nam == "lname") {
      setFormChange({
        lname: val,
      });
    }

    if (nam == "email") {
      setFormChange({
        email: val,
      });
    }

    if (nam == "number") {
      setFormChange({
        number: val,
      });
    }
  };

  return (
    <>
      <div className="register-form">
        <form onSubmit={console.log(formChange)}>
          <input
            // value=""
            type="text"
            placeholder="First Name"
            name="fname"
            onChange={change}
          />

          <input
            // value=""
            type="text"
            placeholder="Middle Name"
            name="mname"
            onChange={change}
          />

          <input
            // value=""
            type="text"
            placeholder="Last Name"
            name="lname"
            onChange={change}
          />

          <button>Submit</button>
        </form>

        <h4>{formChange.fname}</h4>
      </div>
    </>
  );
};

export default Register;
