import React, { useState } from "react";
import "./Register.css";

const Register = () => {
  const [formChange, setFormChange] = useState({
    fname: "",
    mname: "",
    lname: "",
    email: "",
    number: "",
  });

  const [finalChange, setFinalChange] = useState({
    fname: "",
    mname: "",
    lname: "",
    email: "",
    number: "",
  });

  const change = (event) => {
    event.preventDefault();
    const val = event.target.value;
    const nam = event.target.name;

    if (nam == "fname") {
      setFormChange({
        fname: val,
      });
    }

    if (nam == "mname") {
      setFormChange({
        mname: val,
      });
    }

    if (nam == "lname") {
      setFormChange({
        lname: val,
      });
    }

    if (nam == "email") {
      setFormChange({
        email: val,
      });
    }

    if (nam == "number") {
      setFormChange({
        number: val,
      });
    }
  };

  return (
    <>
      <div className="register-form">
        <form onSubmit={console.log(formChange)}>
          <input
            value={formChange.fname}
            type="text"
            placeholder="First Name"
            name="fname"
            onChange={change}
            required
          />

          <input
            value={formChange.mname}
            type="text"
            placeholder="Middle Name"
            name="mname"
            onChange={change}
          />

          <input
            value={formChange.lname}
            type="text"
            placeholder="Last Name"
            name="lname"
            onChange={change}
            required
          />

          <input
            value={formChange.email}
            type="email"
            placeholder="Email Address"
            name="email"
            onChange={change}
            required
          />

          <input
            value={formChange.number}
            type="number"
            placeholder="Phone Number"
            name="number"
            onChange={change}
            required
          />

          <input
            className="photo-input"
            type="file"
            accept="image/png, image/gif, image/jpeg, image/jpg"
            placeholder="Photo"
            name="photo"
            onChange={change}
            required
          />

          <button onClick={() =>{
            setFinalChange({
              prev
            })
          }}>Submit</button>
        </form>
      </div>
    </>
  );
};

export default Register;
