import React from "react";
import "./Check_in.css";
// import Phone from "./Check_In_Components/Phone";
import { useNavigate } from "react-router-dom";
import Location from "./Location";
import { GrFormUpload } from "react-icons/gr";

const Check_in = () => {
  // const change = (event) => {
  //   const val = event.target.files[0];
  // };

  const [name, setName] = useState("");
  const [mobile, setMobile] = useState("");
  const [email, setEmail] = useState("");
  const [photo, setPhoto] = useState(null);

  const handleSubmit = (event) => {
    event.preventDefault();

    const formData = new FormData();
   
    formData.append("mobile_no", mobile);
    formData.append("email", email);
    formData.append("file", photo);

    fetch(
      "https://faceapiv-service.ubsbto1gdi65m.us-west-2.cs.amazonlightsail.com/register",
      {
        method: "POST",
        body: formData,
      }
    )
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        // handle success response
        alert("User Registered Successfully!");
      })
      .catch((error) => {
        console.error("There was a problem with the fetch operation:", error);
      });
  };

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleMobileChange = (event) => {
    setMobile(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePhotoChange = (event) => {
    setPhoto(event.target.files[0]);
  };

  const navigate = useNavigate();
  return (
    <>
      <div className="phone_number">
        <div className="check_in">
          <div className="phone-number">
            <label>Enter your Phone Number:</label>
            <input type="text" placeholder="Phone Number" />

            <div className="photo-field">
              <div
                style={{
                  display: "flex",
                }}
              >
                <i
                  className="image-icon"
                  for="firstimg"
                  style={{ color: "white" }}
                >
                  <GrFormUpload />
                </i>
                <input
                  className="photo-input"
                  id="firstimg"
                  type="file"
                  accept="image/png, image/jpeg, image/jpg"
                  // placeholder="Photo"
                  autoComplete="off"
                  name="photo"
                  onChange={change}

                  // required
                />
                <p className="photo-title">Upload your Photo here</p>
              </div>
            </div>
          </div>
        </div>

        <div className="below-buttons">
          <button
            className="next"
            onClick={() => {
              navigate("/location");
              // <Location />
            }}
          >
            Next
          </button>
        </div>
      </div>
      {/* </div> */}
      {/* </div> */}
    </>
  );
};

export default Check_in;
